        /**
         * 创建付款型号记录
         * @param {*} t //order / refund
         * @param {*} acc 
         * @param {*} entity 
         * @param {*} T_memo  备注型号
         * @param {*} fm  // 期 月份
         */
        function CreatePaymentRec(t, acc, entity, T_memo, fm, currency, deposit_date, num) {
            log.debug("deposit_date:" + deposit_date, entity + "," + T_memo + "," + fm + "," + currency)
            var resd, qty, Tmemo, rs;
            search.create({
                type: "customrecord_payment_rec_store",
                filters: [{
                        name: 'custrecord_payment_type',
                        operator: search.Operator.IS,
                        values: t
                    },
                    {
                        name: 'custrecord_payment_store',
                        operator: "anyof",
                        values: acc
                    },
                    {
                        name: "custrecord_payment_customer",
                        operator: "anyof",
                        values: entity
                    },
                    {
                        name: "custrecord_payment_fm",
                        operator: "is",
                        values: fm
                    },
                    {
                        name: "custrecord_payment_memo_currency",
                        operator: "is",
                        values: currency
                    },
                    {
                        name: "custrecord_payment_resovle",
                        operator: "is",
                        values: false
                    }
                ],
                columns: [{
                        name: "internalid",
                        sort: "DESC"
                    },
                    {
                        name: "custrecord_payment_counts"
                    },
                    {
                        name: "custrecord_payment_memo"
                    }
                ]
            }).run().each(function (rec) {
                Tmemo = rec.getValue("custrecord_payment_memo")
                resd = record.load({
                    type: "customrecord_payment_rec_store",
                    id: rec.id
                })
                qty = Number(rec.getValue("custrecord_payment_counts")) + num
            })
            //付款、退款备注编号，大于5000则增加流水号 T_memo +""
            if (qty <= 4000 && qty) {
                rs = Tmemo
                resd.setValue({
                    fieldId: "custrecord_payment_memo",
                    value: Tmemo
                })
                resd.setValue({
                    fieldId: "custrecord_payment_counts",
                    value: qty
                })
            } else if (qty > 4000 && qty) {
                qty = num
                var ty = Number(Tmemo.split("-")[1]) + num
                Tmemo = Tmemo.split("-")[0] + "-" + ty
                rs = Tmemo
                resd = record.create({
                    type: "customrecord_payment_rec_store"
                })
                resd.setValue({
                    fieldId: "custrecord_payment_memo",
                    value: Tmemo
                })
                resd.setValue({
                    fieldId: "custrecord_payment_counts",
                    value: qty
                })
            } else if (!resd) {
                qty = num
                rs = T_memo
                resd = record.create({
                    type: "customrecord_payment_rec_store"
                })
                resd.setValue({
                    fieldId: "custrecord_payment_memo",
                    value: T_memo
                })
                resd.setValue({
                    fieldId: "custrecord_payment_counts",
                    value: qty
                })
            }
            resd.setValue({
                fieldId: "custrecord_payment_fm",
                value: fm
            })
            resd.setValue({
                fieldId: "name",
                value: rs
            })
            resd.setValue({
                fieldId: "custrecord_payment_type",
                value: t
            })
            resd.setValue({
                fieldId: "custrecord_payment_store",
                value: acc
            })
            resd.setValue({
                fieldId: "custrecord_payment_customer",
                value: entity
            })
            resd.setValue({
                fieldId: "custrecord_payment_memo_currency",
                value: currency
            })
            if (deposit_date)
                resd.setText({
                    fieldId: "custrecord_pay_deposite_date",
                    text: deposit_date
                })
            var sds = resd.save()
            log.debug("OK:" + sds, rs)
            return rs
        }

        /**
         * 检查日记账是否存在
         * @param {*} orderid 
         * @param {*} settlmentid +month 
         * @param {*} settlement_ids 
         * @param {*} memomain 
         */
        function CheckJO(orderid, settlmentid, settlement_ids, memomain, acc) {
            log.debug("memomain:" + memomain, "settlement_ids：" + settlement_ids)
            log.debug("search 的 settlmentid:" + settlmentid, acc)
            var getc
            search.create({
                type: 'journalentry',
                filters: [{
                        name: 'mainline',
                        operator: 'is',
                        values: true
                    },
                    {
                        name: 'custbody_amazon_settlementid',
                        operator: 'is',
                        values: settlmentid
                    },
                    {
                        name: 'custbody_jour_orderid',
                        operator: 'is',
                        values: orderid
                    },
                    {
                        name: 'memomain',
                        operator: 'is',
                        values: memomain
                    }
                ]
            }).run().each(function (e) {
                getc = true
                log.debug("00000该 settlement id 已结算 " + settlmentid, e.id + "," + orderid)
                return false
            })
            if (getc) {
                log.debug("已存在凭证", orderid + "," + settlmentid)
                settlement_ids.map(function (seid) {
                    record.submitFields({
                        type: "customrecord_aio_amazon_settlement",
                        id: seid,
                        values: {
                            custrecord_settle_is_generate_voucher: true
                        }
                    })
                })
                return false
                //结算修复设置T
            } else {
                var ssd = []
                try {
                    var memomain3
                    if (memomain.indexOf("re") == -1) memomain3 = "03"
                    else memomain3 = "re03"
                    search.create({
                        type: 'journalentry',
                        filters: [{
                                name: 'mainline',
                                operator: 'is',
                                values: true
                            },
                            // { name: 'custbody_amazon_settlementid', operator: 'is', values: settlmentid.split("-")[0] },
                            {
                                name: 'custbody_jour_orderid',
                                operator: 'is',
                                values: orderid
                            },
                            {
                                name: 'custbody_pr_store',
                                operator: 'anyof',
                                values: acc
                            },
                            {
                                name: 'memomain',
                                operator: 'is',
                                values: memomain
                            }
                        ],
                        columns: [{
                            name: "internalid",
                            summary: "GROUP"
                        }]
                    }).run().each(function (e) {
                        ssd.push(e.getValue(e.columns[0]))
                        return true
                    })
                    if (ssd.length > 0) {
                        search.create({
                            type: 'journalentry',
                            filters: [{
                                    name: 'mainline',
                                    operator: 'is',
                                    values: true
                                },
                                // { name: 'custbody_amazon_settlementid', operator: 'is', values: settlmentid.split("-")[0] },
                                {
                                    name: 'custbody_jour_orderid',
                                    operator: 'is',
                                    values: orderid
                                },
                                {
                                    name: 'custbody_pr_store',
                                    operator: 'anyof',
                                    values: acc
                                },
                                {
                                    name: 'memomain',
                                    operator: 'is',
                                    values: memomain3
                                }
                            ],
                            columns: [{
                                name: "internalid",
                                summary: "GROUP"
                            }]
                        }).run().each(function (e) {
                            ssd.push(e.getValue(e.columns[0]))
                            return true
                        })
                        log.debug("寻找之前settlmentid 生成的日记账:", ssd)
                        ssd.map(function (ds) {
                            record.delete({
                                type: "journalentry",
                                id: ds
                            })
                        })
                    }
                } catch (error) {}
                return true
            }
        }
